mapper.ref:
SELECT create_time FROM sys_user
            WHERE 1=1
            {% if name %}
            AND name = :name
            {% endif %}

mapper.select:
SELECT * FROM t_user
    WHERE 1=1
    {% if name %}
    AND name = {{ name | quote }}
    {% endif %}
    {% if id_list %}
    AND id IN ({{ id_list | join(", ") }})
    {% endif %}
    {$ common_condition $}
